import React, { Component } from 'react';

class FlightInput extends Component {
  state = {};
  render() {
    return (
      <div className="flight-direction">
        <input value={this.props.countrySelection} id={this.props.type} onInput={this.props.onInput} />

       { this.props.showDropDown && <select
          className="drop-down"
          id={this.props.type}
          onChange={this.props.onAirportSelect}
        >
          <option></option>
          {this.props.Places.map((option) => {
            return <option>{option.PlaceName}</option>;
          })}
        </select>}
      </div>
    );
  }
}

export default FlightInput;
