  //  const handleClick = () => setClick(!click);

  render() {
    return (
      <nav className="navbar">
        <div className="nav-container">
          <Link exct to="/" className="nav-logo">
            Nomader
            <i className="fas fa-code"></i>
          </Link>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className="nav-item">
              <Link
                exct
                to="/"
                activeClassName="active"
                className="nav-links"
                onClick={handleClick}
              >
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link
                exct
                to="/contact"
                activeClassName="active"
                className="nav-links"
                // onClick={handleClick}
              >
                Contact
              </Link>
            </li>
            <li className="nav-item">
              <Link
                exct
                to="/flight-deals"
                activeClassName="active"
                className="nav-links"
                // onClick={handleClick}
              >
                Flight Deals
              </Link>
            </li>
            <li className="nav-item">
              <Link
                exct
                to="/hottest-destinations"
                activeClassName="active"
                className="nav-links"
                // onClick={handleClick}
              >
                Hottest Destinations
              </Link>
            </li>
          </ul>
          {/* <div className="nav-icon" onClick={handleClick}>
          <i className={click ? "fas fa-times": "fas fa-bars"}></i>
          </div> */}
        </div>
      </nav>
    );
  }
}

export default Nav;
